unitsize(1 cm);

pair A, B, C, D, E, F, O;

O = (0,0);
A = (1,0);
B = rotate(60)*(A);
C = 2*(A);
D = 2*(B);
E = 2*rotate(60)*(A);
F = 2*rotate(120)*(A);

draw(O--A--B--C--D--E--F--O);
draw(O--C--E);
draw(O--A--D);

